    console.log(products)

    return (
        <>
            <div>
                {cards}
            </div>
            <div className="featured-store">
                <h1>Stores</h1>
                <h2>{featuredStore.name}</h2>
                <h3>Revenue: </h3>
                <h3>Net Profit: </h3>
                <h3>Products: ({inventoryByStore.length}) </h3>
