import React, { useEffect, useState } from "react";
import NavBar from "./NavBar";
import Sales from "./Sales";
import Products from "./Products";
import Stores from "./Stores";
import { Switch, Route } from 'react-router-dom';

function App() {

    const [salesArray, setSalesArray] = useState([])

    const [productsArray, setProductsArray] = useState([])
    const [inventoryByStore, setInventoryByStore] = useState({})

    console.log(storesArray)

    useEffect(() => {
        fetch("/sales")
        .then(resp=>resp.json())
        .then((data)=>(setSalesArray(data)))

    }, [])



    useEffect(() => {
        fetch("/products")
        .then(resp=>resp.json())
        .then((data)=>(setProductsArray(data)))

    }, [])

    // useEffect(() => {
    //     fetch("/1/stores/1") // "http://localhost:3000/<int:company_id>/stores/<int:store_id>"
    //     .then(resp=>resp.json())
    //     .then((data)=>(setInventoryByStore(data)))

    // }, [])

    return (
        <div className="App">
            <NavBar />
            <Switch>
                <Route exact path="/sales">
                    <Sales salesArray={salesArray} setSalesArray={setSalesArray} />
                </Route>
                <Route exact path="/stores">
                    <Stores />
                </Route>
                <Route exact path="/products">
                    <Products productsArray={productsArray} setProductsArray={setProductsArray} />
                </Route>
            </Switch>
        </div>
    );
}

export default App;
