import React, { useEffect, useState } from "react";

function Products() {

    const [productsArray, setProductsArray] = useState([])
    const [inventoryByProduct, setInventoryByProduct] = useState({})
    const [featuredProduct, setFeaturedProduct] = useState({})

    // console.log(featuredProduct)
    // console.log(featuredProduct.id)
    // console.log(productsArray)
    // console.log(inventoryByProduct)

    useEffect(() => {
        fetch(`/1/sales_overview/products`)
        .then(resp=>resp.json())
        .then((data)=>{
            setProductsArray(data)
            setFeaturedProduct(data[0])
        })
    }, [])

    const products = []
    productsArray.forEach(product => {
        const is_in_products = products.some((obj) => (obj.id === product.id)) //check if the products variable has an item in it whose id matches the id of this product
        if (!is_in_products) { //if it does not, add the product to the products array
            products.push(product)
        }
        //if it does, skip it
        }
    )
    console.log(products)

    useEffect(() => {
        fetch(`/products/${featuredProduct.id}`)
        .then(resp=>resp.json())
        .then((data)=>(setInventoryByProduct(data)))
    }, [featuredProduct])

    const cards = products.map((product) => {
        // console.log(product)
        return (
            <div key={product.id} className="info-box">
                <h2 onClick={() => selectProduct(product)}>{product.name}</h2>
                <p>{product.serial_number}</p>
                <button onClick={editProduct}>Edit(Patch)</button>
                <button onClick={deleteProduct}>Delete</button>
            </div>
        )
    })

    function selectProduct(product) {
        console.log("product selected")
        setFeaturedProduct(product)
    }

    function editProduct(e) {
        console.log("edit selected product")
        // console.log(e.target)
    }

    function deleteProduct() {
        console.log("delete selected")
    }

    const stores = <li></li>
    if (inventoryByProduct[0] !== undefined){
        stores = inventoryByProduct.map((inventory_item) => {
            console.log(inventory_item)
            return (
                <li key={inventory_item.id}><span className="stores-subheader">Store:</span> {inventory_item.store.name}
                    <ul>
                        <li>Price: ${inventory_item.price}</li>
                        <li>Manufacturing Cost: ${inventory_item.product.manufacturing_cost}</li>
                        <li>Serial Number: {inventory_item.product.serial_number}</li>
                    </ul>
                </li>
            )
        })}

    return (
        <>
            <div>
                {cards}
            </div>
            <div className="featured-product">
                <h1>Product</h1>
                <h2>{featuredProduct.name}</h2>
                <h3>Revenue: </h3>
                <h3>Sales: </h3>
                <h3>Stores: </h3>
                <ol>
                    {/* {stores} */}
                </ol>
            </div>
        </>
    )
}

export default Products
