import React, { useEffect, useState } from "react";

function Sales() {

    const [salesArray, setSalesArray] = useState([])
    const [productsArray, setProductsArray] = useState([])

    useEffect(() => {
        fetch("/sales")
        .then(resp=>resp.json())
        .then((data)=>(setSalesArray(data)))

    }, [])

    useEffect(() => {
        fetch("/products")
        .then(resp=>resp.json())
        .then((data)=>(setProductsArray(data)))

    }, [])

    // console.log(productsArray)

    function viewProducts(productsArray){
        console.log(productsArray)
    }

    let viewContent = "Choose company item to view"

    return (
    <>
    <div className="sales-sidebar">
        <button>View company revenue</button>
        <button>View all company stores</button>
        <button onClick = {() => viewProducts(productsArray)}>View all company products</button>
    </div>
    <div className="sales-container">
        <div className="sales-information-box">
            <h1>Company Name</h1>
            <p>Welcome, employee</p>
            <p>{viewContent}</p>
        </div>
    </div>
    </>
    )
}

export default Sales
